def get_floor(brackets):
    acc = 0
    for index, brack in enumerate(brackets):
        if brack == "(":
            acc += 1
        else:
            acc -= 1

        if acc == -1:
            print("basement:", index + 1)

    return acc

for test, expected_value in tests.items():
    result = get_floor(test)
    print(test, result, "expected", expected_value)
    assert(result == expected_value)

